package com.jpm.socket;


public class Message {
	private String from;
	private String text;

	public Message() {
		// TODO Auto-generated constructor stub
	}
	
	public String getFrom() {
		return from;
	}
	public void setFrom(String from) {
		this.from = from;
	}

	public String getText() {
		return text;
	}
	public void setText(String text) {
		this.text = text;
	}
	
}
